    public:
        enum {
            NoFieldsSet = 0,
            ContextIdSet = 1 << 1,
            SourceIdSet = 1 << 2,
            DestinationIdSet = 1 << 3,
            AllFieldsSet = (ContextIdSet | SourceIdSet | DestinationIdSet | 0)};


